import '../css/grid.css';
import '../css/gridData.css';

import WorkOrders from '../components/work-orders';

class Parent extends React.Component{

	constructor(props) {
		super(props);
		var array = this.props.data.wo.orders;
		array.sort(function(a,b){
			return a.deadline - b.deadline;
		});
		var orders = array;

       	this.state = {
 			data:orders
       	}
    }



	state = {
		data: this.props.data.wo.orders
	}

	render(){

		return(
			<div>
				<label class="switch">
					<p id='early'>Earliest first</p>
					<input id='sortByDate' type="checkbox" onChange={this.toggle.bind(this)}/>
		  			<span class="slider round"></span>
		  			<p id='late'>Latest first</p>
				</label>
				<div className='grid'>
					{this.state.data.map((wo, i) => <WorkOrders {...this.state.data} key={i} i={i} wo={wo} />)}
				</div>
			</div>
		)
	}

	toggle(){

		if (typeof window !== 'undefined') {
			var c = document.getElementById('sortByDate');
			var array = this.props.data.wo.orders;
			if(c.checked){
				array.sort(function(a,b){
					return b.deadline - a.deadline;
				});
				var orders = array;
				this.setState({data:orders});
			}
			else{
				array.sort(function(a,b){
					return a.deadline - b.deadline;
				});
				var orders = array;
				this.setState({data:orders});
			}
		}
	}

}

export default Parent;
