import '../css/grid.css';
import '../css/gridData.css';

import parsedWorkers from '../data/worker.js';

class WorkOrders extends React.Component{

	constructor(){
	    super()

	    this.state = {

	    }
	}

	state = {

	}

	render(){
		let select = '';
		const worker = this.props.wo.workerId;
		console.log(parsedWorkers);
		var result = parsedWorkers.filter(obj => {
		  return obj.id === worker
		})

		this.setState({companyName:result.worker.companyName, email:result.worker.email, image:result.worker.image, name:result.worker.name})

		// const url='https://www.hatchways.io/api/assessment/workers/'+worker;
		// fetch(url)
		// 	.then(response => response.json())
		//   	.then((jsonData) => {
		//   		this.setState({companyName:jsonData.worker.companyName, email:jsonData.worker.email, image:jsonData.worker.image, name:jsonData.worker.name})
		//   	})
		//   	.catch((error) => {
		//     	console.error(error)
		//   	})

		if (typeof window !== 'undefined') {
			var serachField = document.getElementById('searchField');
			var input = document.getElementById('searchField').value; 

			if(input === this.state.name){
				select=
					<div className='grid-figure-wrap'>
						<p className='workId'>{(this.props.i)+1}</p>
						<h2 className='workHeading'>{this.props.wo.name}</h2>
						<p className='workDescription'>{this.props.wo.description}</p>
						<div>
							<p className='workerId'>WORKER ID:{this.props.wo.workerId}</p>
							<p className='workerName'>{this.state.name}</p>
							<p className='workerEmail'>E-MAIL:{this.state.email}</p>
							<p className='workerCompany'>{this.state.companyName}</p>
							<img className='workerPhoto' src={this.state.image} />
						</div>
						<p className='workDeadLine'>{this.props.wo.deadline}</p>
					</div>
			}
			else if(input === ''){
				select=
					<div className='grid-figure-wrap'>
						<p className='workId'>{(this.props.i)+1}</p>
						<h2 className='workHeading'>{this.props.wo.name}</h2>
						<p className='workDescription'>{this.props.wo.description}</p>
						<div>
							<p className='workerId'>WORKER ID:{this.props.wo.workerId}</p>
							<p className='workerName'>{this.state.name}</p>
							<p className='workerEmail'>E-MAIL:{this.state.email}</p>
							<p className='workerCompany'>{this.state.companyName}</p>
							<img className='workerPhoto' src={this.state.image} />
						</div>
						<p className='workDeadLine'>{this.props.wo.deadline}</p>
					</div>
			}
			else{
				select;
			}

		}

		return(
			<figure  className='grid-figure'>
			{select}
			</figure>
		)
	}



}

export default WorkOrders;
